import { loadMercadoPago } from "@mercadopago/sdk-js";

await loadMercadoPago();
const mp = new window.MercadoPago("YOUR_PUBLIC_KEY");

export default function FormularioMercadoPago() {
  (async function getIdentificationTypes() {
    try {
      const identificationTypes = await mp.getIdentificationTypes();
      const identificationTypeElement = document.getElementById(
        "form-checkout__identificationType"
      );

      createSelectOptions(identificationTypeElement, identificationTypes);
    } catch (e) {
      return console.error("Error getting identificationTypes: ", e);
    }
  })();

  function createSelectOptions(
    elem,
    options,
    labelsAndKeys = { label: "name", value: "id" }
  ) {
    const { label, value } = labelsAndKeys;

    elem.options.length = 0;

    const tempOptions = document.createDocumentFragment();

    options.forEach((option) => {
      const optValue = option[value];
      const optLabel = option[label];

      const opt = document.createElement("option");
      opt.value = optValue;
      opt.textContent = optLabel;

      tempOptions.appendChild(opt);
    });

    elem.appendChild(tempOptions);
  }

  return (
    <div>
      <h1>Informações para Cadastro</h1>
      <form id="form-checkout" action="/process_payment" method="post">
        <div>
          <div>
            <label htmlFor="payerFirstName">Nome</label>
            <input
              id="form-checkout__payerFirstName"
              name="payerFirstName"
              type="text"
            />
          </div>
          <div>
            <label htmlFor="payerLastName">Sobrenome</label>
            <input
              id="form-checkout__payerLastName"
              name="payerLastName"
              type="text"
            />
          </div>
          <div>
            <label htmlFor="email">E-mail</label>
            <input id="form-checkout__email" name="email" type="text" />
          </div>
          <div>
            <label htmlFor="identificationType">Tipo de documento</label>
            <select
              id="form-checkout__identificationType"
              name="identificationType"
              type="text"
            ></select>
          </div>
          <div>
            <label htmlFor="identificationNumber">Número do documento</label>
            <input
              id="form-checkout__identificationNumber"
              name="identificationNumber"
              type="text"
            />
          </div>
        </div>

        <div>
          <div>
            <input
              type="hidden"
              name="transactionAmount"
              id="transactionAmount"
              value="100"
            />
            <input
              type="hidden"
              name="description"
              id="description"
              value="Nome do Produto"
            />
            <br />
            <button type="submit">Pagar</button>
          </div>
        </div>
      </form>
    </div>
  );
}
